package SlidingWindow;

import java.util.Arrays;
import java.util.HashMap;

public class t567 {
    public boolean checkInclusion(String s1, String s2){
        int[] window = new int[26];
        int[] need = new int[26];
        int left = 0, right = 0, valid = 0;
        for(char c: s1.toCharArray()){
            need[c - 'a'] += 1;
        }
        int required = 0;
        for(int i = 0; i < need.length; i++){
            if(need[i] != 0) required += 1;
        }
        while(right < s2.length()){
            char curr = s2.charAt(right);
            right += 1;
            if(need[curr - 'a'] == 0){
                left = right;
                valid = 0;
                Arrays.fill(window, 0);
                continue;
            }
            window[curr - 'a'] += 1;
            if(window[curr - 'a'] == need[curr - 'a']) valid += 1;
            while(window[curr - 'a'] > need[curr - 'a']){
                char prev = s2.charAt(left);
                if(window[prev - 'a'] == need[prev - 'a']) valid -= 1;
                window[prev - 'a'] -= 1;
                left += 1;
            }
            if(valid == required) return true;
        }
        return false;
    }

    public static void main(String[] args){
        String s1 = "ckzviabspcfbabslodcxtzanlsnwbqrozvnfadhtskosxhxaxzwcthvirwivsfuyxgkdvvdmmaoohvnfegkzdajhzibfiux" +
                "vsihpseyyiapmgldyojfselzzudzrxcksvfoqoeimyfhnvidryqhhpvjawwpzspcfnvmewnhcbbbwfjifmcuspbrrjyhcusyiuyds" +
                "cwenyicdlfzaoaiudsdyjmhmzwmeaozheiaddevjrrgsfgqnyeoyxfvktctazsfizkynlookeorzmdvtloyfpbqjgzhjvykdthwku" +
                "ubnbalrddjxpizysaptrinlytecouekkpsfzbjaitckodcdinydtaaakbntwbvrcabylllxtgtkdqfkjggvbrnxnvcsfpcutabvazy" +
                "";
        String s2 = "";
        t567 test = new t567();
        System.out.println(test.checkInclusion(s1, s2));
    }
}
